member.summary:
 Extensions for configuring a test RabbitMQ instance that can be used
 in the test. See  docs.

 Sample usage:

using Magnum.TestFramework;
using MassTransit.Testing;
[Scenario]
public class Using_the_handler_test_factory
{
    IHandlerTest<A> _test;

    [When]
    public void Setup()
    {
        _test = TestFactory.ForHandler<A>()
            .New(x =>
                {
                    x.UseRabbitMqBusScenario();
                    x.Send(new A());
                    x.Send(new B());
                });
        _test.Execute();
    }
    [Finally]
    public void Teardown()
    {
        _test.Dispose();
        _test = null;
    }
    [Then]
    public void Should_have_received_a_message_of_type_a()
    {
        _test.Received.Select<A>().ShouldBeTrue();
    }
}
